sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading && !g_form.isNewRecord()  || newValue === '' || newValue == oldValue) {
        return;
    }
	//获取当前的数据的比赛名称
	g_form.getReference('game_name' , doAlert);
}
function doAlert(record) { // reference is passed into callback as first arguments
		//调用AJAX方法
		var ps = new GlideAjax('GetPersonsOfRecord');
		//设定调用的方法名称
		ps.addParam('sysparm_name','getPersons');
		//设定传入参数
		ps.addParam('sysparm_recordId',record.number);
		//指定回调函数
		ps.getXML(getps);

		//AJAX回调函数
		function getps(response){
			//获取AJAX方法的结果
			var persons = response.responseXML.documentElement.getAttribute("answer");
			//根据返回的json串，取得比赛中的用户人数。
			var personNum = 0;
			for(var item in persons.split(',')){
				personNum++;
			}
			//清除原有表单数据
			g_form.clearValue('perosn_num');
			//在表单中设定新数据
			g_form.setValue('perosn_num',persons.split(',').length);
		}
}
