sys_script_include.script:
//创建类对象
var GetPersonsOfRecord = Class.create();
//继承AJAX对象
GetPersonsOfRecord.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	//设定回调函数
	getPersons: function() {
			//取得全部数据
			var record = new GlideRecord("x_532560_sport_record");
			//根据入力参数取得指定数据
			record.get(this.getParameter('sysparm_recordId'));
			//返回查询对象
			return record.game_persons;
		},
    type: 'GetPersonsOfRecord'
});
